var $mainNav=$('.nav-bar');var mainNavTopPos=$mainNav.offset().top;$(window).scroll(function(){if($(window).scrollTop()>mainNavTopPos){$mainNav.addClass('sticky')}else{$mainNav.removeClass('sticky')}});$('.tutorial > .open').hide();var open=$('.tutorial h2');open.click(function(){$(this).next().slideToggle()});var sliderImages=$('.slider-cross-fade img');var numberOfImages=sliderImages.length;sliderImages.hide();rotatePics(1);function rotatePics(currentPhoto){currentPhoto=currentPhoto%numberOfImages;sliderImages.eq(currentPhoto).fadeOut(function(){sliderImages.each(function(i){$(this).css('z-index',(((numberOfImages-i)+currentPhoto)%numberOfImages))});$(this).siblings().show();setTimeout(function(){rotatePics(++currentPhoto)},1000)})}